mapper.select:
SELECT HID, CASE
WHEN #{startTime}>START_TIME THEN #{startTime} ELSE DATE_FORMAT(START_TIME,'%Y-%m-%d') END AS START_TIME, CASE WHEN #{endTime}<END_TIME THEN #{endTime} ELSE DATE_FORMAT(END_TIME,'%Y-%m-%d') END AS END_TIME FROM CON_CONTRACT WHERE HID=#{hid} AND STATUS IN (5,9,10,13) AND DELETE_FLG = 0 AND ( (START_TIME<=#{startTime} AND END_TIME>=#{endTime}) OR (END_TIME>=#{startTime} AND END_TIME<=#{endTime}) OR (START_TIME>=#{startTime} AND START_TIME<=#{endTime}) )
AND ID != #{id}

SELECT COUNT(1) FROM CON_CONTRACT WHERE 1=1 AND DELETE_FLG = 0
AND BEID IN (${scope})
AND BEID = #{beid}
AND STATUS = #{status}
AND ID NOT IN (SELECT DEL_ID FROM USR_DEL_INFO WHERE UEP_ID = #{uid}) AND ((SOURCE = 2) OR (SOURCE != 2 AND STATUS IN (4,5,6,7,8)))
