mapper.select:
select A.*, (CASE WHEN CIRCLE_ID in
#{area.tradeId}
('')
THEN ROUND((A.PUBLISH_TIME - STR_TO_DATE(#{currentTime},'%Y-%m-%d %H:%i:%s')) ) WHEN CIRCLE_ID not in
#{area.tradeId}
('')
and PARENT_ID in
#{area.parentId}
('')
THEN ROUND((A.PUBLISH_TIME - STR_TO_DATE(#{currentTime},'%Y-%m-%d %H:%i:%s')) ) + 30 ELSE ROUND((A.PUBLISH_TIME - STR_TO_DATE(#{currentTime},'%Y-%m-%d %H:%i:%s')) ) + 60 END ) TIME FROM ( SELECT A.ID, UID, A.PID, A.NAME, A.GENDER, A.PHONE, A.COMMUNITY, A.CIRCLE_ID, (SELECT S.PARENT_ID FROM SYS_TRADE_AREAS S WHERE S.TRADE_ID = A.CIRCLE_ID) PARENT_ID, (SELECT AREA FROM SYS_AREA_AREAS WHERE AREA_ID = (SELECT S.PARENT_ID FROM SYS_TRADE_AREAS S WHERE S.TRADE_ID = A.CIRCLE_ID)) PARENT_NAME, (SELECT NAME FROM SYS_TRADE_AREAS WHERE TRADE_ID = A.CIRCLE_ID) CIRCLE_NAME, A.PUBLISH_TIME, A.STATUS, CREATE_USER, CREATE_USER_IDENTITY, CREATE_TIME, MODIFY_USER, MODIFY_USER_IDENTITY, MODIFY_TIME, DELETE_FLG, VERSION FROM HOU_ENTRUST_INFO A WHERE A.DELETE_FLG = 0 AND A.STATUS = 0 AND (DATE_ADD(A.LOCK_TIME, INTERVAL 30 DAY_MINUTE) < SYSDATE() OR ISNULL(a.LOCK_TIME) OR A.LOCK_EID = #{lockEid}) AND EXISTS ( SELECT B.COMP_ID FROM HOU_ENTRUST_COMP_LINK B WHERE B.MAIN_ID = A.ID AND B.DELETE_FLG = 0 AND B.COMP_ID = #{compId} ) ) A
ORDER BY ${orderBy}
ORDER BY A.PUBLISH_TIME DESC

SELECT
	COUNT(1) COUNT
from HOU_ENTRUST_INFO A WHERE DELETE_FLG = 0 AND A.STATUS = 0
AND EXISTS (
	SELECT B.COMP_ID FROM HOU_ENTRUST_COMP_LINK B WHERE B.MAIN_ID = A.ID AND B.DELETE_FLG = 0 AND B.COMP_ID = #{compId}
)
