mapper.select:
SELECT DISTINCT

FROM sys_menu a
LEFT JOIN sys_menu p ON p.id = a.parent_id
JOIN sys_role_menu rm ON rm.menu_id = a.id
JOIN sys_role r ON r.id = rm.role_id AND r.useable='1'
JOIN sys_user_role ur ON ur.role_id = r.id
JOIN usr_user u ON u.id = ur.user_id AND u.id = #{userId}
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND r.del_flag = #{DEL_FLAG_NORMAL} AND u.DELETE_FLG = #{DEL_FLAG_NORMAL} 

AND a.parent_id=#{id}


ORDER BY a.sort ,a.parent_ids
