mapper.select:
    select 

    from sys_message s
    where PINDEX=(select min(m.PINDEX) from Sys_Message m where m.PINDEX > #{pindex,jdbcType=VARCHAR} and m.RECEIVER_UID = #{receiverUid,jdbcType=VARCHAR}
	    and m.DELETE_FLG = 0
	    ) 
    and s.RECEIVER_UID = #{receiverUid,jdbcType=VARCHAR}
    and s.DELETE_FLG = 0

    select 

    from sys_message s
    where PINDEX=(select max(m.PINDEX) from Sys_Message m where m.PINDEX < #{pindex,jdbcType=VARCHAR} and m.RECEIVER_UID = #{receiverUid,jdbcType=VARCHAR}
	    and m.DELETE_FLG = 0
	    ) 
    and s.RECEIVER_UID = #{receiverUid,jdbcType=VARCHAR}
    and s.DELETE_FLG = 0
