mapper.select:
SELECT count(1) FROM (SELECT N.CMID AS CMID,
, CASE WHEN ISNULL(DISS_AGR_TIME) THEN END_TIME ELSE DISS_AGR_TIME END AS END_TIME, C.STAGES FROM CON_CONTRACT C LEFT JOIN CON_CONCOM_LINK N ON N.CNID = C.ID WHERE 1=1 AND ((SOURCE = 3 OR SOURCE = 4) OR (SOURCE != 3 AND SOURCE != 4 AND STATUS IN (4,5,6,7,8))) AND ID NOT IN (SELECT DEL_ID FROM USR_DEL_INFO WHERE UEP_ID = #{currUserId})
AND LESSOR_PID = #{lessorPid} AND STATUS NOT IN (1,9,10)
AND LESSEE_PID = #{lesseePid}
AND HID = #{hid}
) D LEFT JOIN HOU_HOUSE_INFO H ON H.ID =D.HID

SELECT D.ID, D.HID, D.CMID, , D.STAGES, H.CERT_STATUS certStatus FROM (SELECT N.CMID AS CMID, , CASE WHEN ISNULL(DISS_AGR_TIME) THEN END_TIME ELSE DISS_AGR_TIME END AS END_TIME, C.STAGES FROM CON_CONTRACT C LEFT JOIN CON_CONCOM_LINK N ON N.CNID = C.ID WHERE 1=1 AND ((SOURCE = 3 OR SOURCE = 4) OR (SOURCE != 3 AND SOURCE != 4 AND STATUS IN (4,5,6,7,8))) AND ID NOT IN (SELECT DEL_ID FROM USR_DEL_INFO WHERE UEP_ID = #{currUserId})
AND LESSOR_PID = #{lessorPid} AND STATUS NOT IN (1,9,10)
AND LESSEE_PID = #{lesseePid}
AND HID = #{hid}
) D LEFT JOIN HOU_HOUSE_INFO H ON H.ID =D.HID
ORDER BY ${orderBy}
ORDER BY START_TIME DESC
