SELECT count(1) FROM( SELECT L.HID, L.ID, A.NO LNO,
(CASE WHEN (L.TITLE IS NULL OR L.TITLE = '') THEN ( CONCAT(l.DISTRICT_NAME,' ', (case when (l.VILLAGE_NAME is null or l.VILLAGE_NAME = '') THEN '' ELSE CONCAT(l.VILLAGE_NAME,' ') END), SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', 1) ,'室', SUBSTRING(L.APARTMENT_LAYOUT,3,1) ,'厅', SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', -1) ,'卫 ', SUBSTRING_INDEX(a.BUILDING_AREA,'.', 1) ,'平 ', (CASE l.LEASE_MODE WHEN '1' THEN '整租' ELSE '合租' END) ) ) ELSE L.TITLE END) TITLE,
L.DISTRICT, SUBSTRING_INDEX(L.DISTRICT,',', 1) DISTRICT_AREA, SUBSTRING_INDEX(L.DISTRICT,',', -1) DISTRICT_DIST, L.APARTMENT_LAYOUT, L.FLOOR, L.RENT, L.SOURCE_TYPE, L.LEASE_MODE, L.DECOR, L.FEATURE, SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', 1) APARMENT_ROOM, L.MODIFY_TIME, L.CREATE_TIME, A.CERT_STATUS, L.VILLAGE_NAME, L.ELECTRIC, L.STATIONID, L.FRESH_TIME, L.AUDIT_STATUS FROM HOU_HOUSE_INFO A,HOU_LEASE_INFO L WHERE A.ID = L.HID AND A.DELETE_FLG = 0 AND L.RELEASE_STATUS = 1 AND L.AUDIT_STATUS != 2 AND A.AUDIT_STATUS = 2 AND A.LOCK_STATUS = 0 AND A.ACTIVE_STATUS = 1
AND SUBSTRING_INDEX(L.DISTRICT,',', 1) = #{districtArea}
AND L.SOURCE_TYPE = #{sourceType}
AND L.LEASE_MODE = #{leaseMode}
AND SUBSTRING_INDEX(L.DISTRICT, ',', - 1) = #{districtDist}
AND L.RENT < 500
AND L.RENT BETWEEN 500 AND 1000
AND L.RENT BETWEEN 1000 AND 1500
AND L.RENT BETWEEN 1500 AND 2000
AND L.RENT BETWEEN 2000 AND 2500
AND L.RENT > 2500
AND L.RENT >= #{startRent}
AND L.RENT <= #{endRent}
AND SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', 1) > 3
AND SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', 1) = #{aparmentRoom}
AND L.DECOR = #{decor}
AND (
L.FEATURE like CONCAT('%',#{item},'%')
OR L.FEATURE like CONCAT('%',#{item},'%')
)
AND L.STATIONID in ( ${lines} )
AND L.STATIONID = #{station}
AND (
TITLE like CONCAT('%',#{content},'%')
OR L.DISTRICT_NAME like CONCAT('%',#{content},'%')
OR L.VILLAGE_NAME like CONCAT('%',#{content},'%') )
) A

