ORDER BY ${orderBy}

SELECT L.HID, L.ID, A.NO LNO,
(CASE WHEN (L.TITLE IS NULL OR L.TITLE = '') THEN ( CONCAT(l.DISTRICT_NAME,' ', (case when (l.VILLAGE_NAME is null or l.VILLAGE_NAME = '') THEN '' ELSE CONCAT(l.VILLAGE_NAME,' ') END), SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', 1) ,'室', SUBSTRING(L.APARTMENT_LAYOUT,3,1) ,'厅', SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', -1) ,'卫 ', SUBSTRING_INDEX(a.BUILDING_AREA,'.', 1) ,'平 ', (CASE l.LEASE_MODE WHEN '1' THEN '整租' ELSE '合租' END) ) ) ELSE L.TITLE END) TITLE,
(SELECT NAME FROM SYS_CODE WHERE TYPE = 'rental_mode' AND CODE = L.LEASE_MODE) LEASE_MODE_NAME, (SELECT NAME FROM SYS_CODE WHERE TYPE = 'hou_decor' AND CODE = L.DECOR) DECOR_NAME, L.DISTRICT, SUBSTRING_INDEX(L.DISTRICT,',', 1) DISTRICT_AREA, SUBSTRING_INDEX(L.DISTRICT,',', -1) DISTRICT_DIST, (SELECT AREA FROM SYS_AREA_AREAS WHERE AREA_ID = SUBSTRING_INDEX(L.DISTRICT,',', 1)) DISTRICT_AREA_NAME, (SELECT NAME FROM SYS_TRADE_AREAS S WHERE S.TRADE_ID = SUBSTRING_INDEX(L.DISTRICT,',', -1) ) DISTRICT_DIST_NAME, L.APARTMENT_LAYOUT, L.FLOOR, L.RENT, L.SOURCE_TYPE, L.LEASE_MODE, L.DECOR, L.FEATURE, SUBSTRING_INDEX(L.APARTMENT_LAYOUT,',', 1) APARMENT_ROOM, L.MODIFY_TIME, L.CREATE_TIME, (SELECT COUNT(1) COUNT FROM USR_HOU_COLLECTION WHERE HID = A.ID ) COLLECTION_COUNT, A.CERT_STATUS, L.VILLAGE_NAME, L.DESCRIPTION, (SELECT NAME FROM SYS_CODE WHERE TYPE = 'hou_orientation' AND CODE = L.ORIENTATION) ORIENTATION, L.REQUIREMENT, L.OWNER_COST_ITEMS, L.OWNER_COST, A.BEID AS EID, (SELECT COUNT(U.ID) FROM USR_HOU_COLLECTION U WHERE U.HID = A.ID AND U.UID = #{eid}) IS_COLLECT, (SELECT COUNT(U.ID) FROM USR_HOU_COLLECTION U WHERE U.HID = A.ID ) COLLECT_COUNT, A.DELETE_FLG, A.UID, L.TEL, L.ELECTRIC, (SELECT NAME FROM SYS_CODE WHERE TYPE = 'build_time' AND CODE = L.BUILD_TIME) BUILD_TIME, (SELECT NAME FROM SYS_CODE WHERE TYPE = 'decor_time' AND CODE = L.DECOR_TIME) DECOR_TIME, (CASE WHEN L.LEASE_MODE = '1' THEN (SELECT NAME FROM SYS_CODE WHERE TYPE = 'rent_deposit' AND CODE = SUBSTRING_INDEX(L.rent_Type,',', 1)) END)RENT_DEPOSIT, (CASE WHEN l.LEASE_MODE = '1' THEN (SELECT NAME FROM SYS_CODE WHERE TYPE = 'rent_payment' AND CODE = SUBSTRING_INDEX(L.rent_Type,',', -1)) END) RENT_PAYMENT, L.PREFERENTIAL, L.FEATURE, L.FACILITIES, L.CHECKIN_TIME, A.HOUSE_ADDR, A.BUILDING_AREA, L.STAGES, A.AUDIT_STATUS houseAuditStatus, L.RELEASE_STATUS, A.ACTIVE_STATUS, A.LOCK_STATUS, L.ONLINE_PAYMENT FROM HOU_HOUSE_INFO A LEFT JOIN HOU_LEASE_INFO L ON A.ID = L.HID
AND L.ID = #{id}
AND A.ID = #{hid}
and A.AUDIT_STATUS = #{houseAuditStatus}
AND A.Active_Status != #{activeStatus}
AND A.lock_Status != #{lockStatus}
AND L.RELEASE_STATUS = #{releaseStatus}

