	/**
	 * 保存业务日志
	 * 
	 * @param id 业务报主键ID
	 * @param drcp 操作描述
	 * @param optEvent 操作事件
	 */
	
	public void saveLog (String id, String drcp, int optEvent, SysBroadbandMapper sysBroadbandMapper, SysBroadbandLogMapper sysBroadbandLogMapper) {
		//插入之后使用id重新查询，获取最新的对象
		SysBroadband sysBroadband = sysBroadbandMapper.selectByPrimaryKey(id);
		//插入日志
		ConversionUtils.reflectionAttr(sysBroadband, this);
		this.setId(IdGen.uuid());
		this.setLid(sysBroadband.getId());
		this.setOperator(UserUtils.getUser().getId());
		this.setOptEvent(optEvent);
		this.setOptTime(DateUtils.getSynchTime());
		this.setDcrp(drcp);
		this.setPindex(null);
		sysBroadbandLogMapper.insertSelective(this);
	}
