select 

from agt_emp_info
where ID = #{id,jdbcType=VARCHAR}

SELECT
    COUNT(1)
FROM
    AGT_EMP_INFO E
LEFT JOIN SYS_PERSON P ON E.PID = P.ID
LEFT JOIN USR_USER U ON E.USER_ID = U.ID
LEFT JOIN SYS_OFFICE O ON E.OFFICE_ID = O.ID
LEFT JOIN SYS_ROLE R ON E.ROLE_CODE = R.ID
WHERE
    E.COMPANY_ID = #{companyId} 
    AND E.DELETE_FLG = #{DEL_FLAG_NORMAL}

        AND (O.PARENT_IDS LIKE '%${parentId}%' OR  E.OFFICE_ID = #{parentId})


        AND E.OFFICE_ID = #{officeId}


        AND E.STATUS = #{status}


        AND E.ROLE_CODE = #{roleCode}


        AND (
            P.`NAME` LIKE '%${selectValue}%'
            OR U.MOBILE LIKE '%${selectValue}%'
        )

SELECT E.ID AS ID, E.COMPANY_ID AS COMPANY_ID, P.`NAME` AS NAME, E.STATUS_TIME AS STATUSTIME, E.`STATUS` AS STATUS, U.MOBILE AS MOBILE, O.`NAME` AS OFFICENAME, R.`NAME` AS ROLENAME, O.PARENT_IDS AS ROLES FROM AGT_EMP_INFO E LEFT JOIN SYS_PERSON P ON E.PID = P.ID LEFT JOIN USR_USER U ON E.USER_ID = U.ID LEFT JOIN SYS_OFFICE O ON E.OFFICE_ID = O.ID LEFT JOIN SYS_ROLE R ON E.ROLE_CODE = R.ID WHERE E.COMPANY_ID = #{companyId} AND E.DELETE_FLG = #{DEL_FLAG_NORMAL}
AND (O.PARENT_IDS LIKE '%${parentId}%' OR E.OFFICE_ID = #{parentId})
AND E.OFFICE_ID = #{officeId}
AND E.STATUS = #{status}
AND E.ROLE_CODE = #{roleCode}
AND ( P.`NAME` LIKE '%${selectValue}%' OR U.MOBILE LIKE '%${selectValue}%' )
order by E.status_time DESC

SELECT E.ID AS ID, E.COMPANY_ID AS COMPANY_ID, P.`NAME` AS NAME, E.STATUS_TIME AS STATUSTIME, E.`STATUS` AS STATUS, U.MOBILE AS MOBILE, O.`NAME` AS OFFICENAME, R.`NAME` AS ROLENAME, O.PARENT_IDS AS ROLES, E.OFFICE_ID AS OFFICE_ID FROM AGT_EMP_INFO E LEFT JOIN SYS_PERSON P ON E.PID = P.ID LEFT JOIN USR_USER U ON E.USER_ID = U.ID LEFT JOIN SYS_OFFICE O ON E.OFFICE_ID = O.ID LEFT JOIN SYS_ROLE R ON E.ROLE_CODE = R.ID WHERE E.COMPANY_ID = #{companyId} AND E.DELETE_FLG = #{DEL_FLAG_NORMAL}
AND (O.PARENT_IDS LIKE '%${parentId}%' OR E.OFFICE_ID = #{parentId})
AND E.OFFICE_ID = #{officeId}
AND E.STATUS = #{status}
AND ( P.`NAME` LIKE '%${selectValue}%' OR U.MOBILE LIKE '%${selectValue}%' )
order by E.status_time DESC

