SELECT A.ID FROM AGT_EMP_INFO A,SYS_OFFICE S WHERE A.OFFICE_ID = S.ID  AND A.DELETE_FLG = 0 AND S.DEL_FLAG = 0 

	AND (OFFICE_ID = #{officeId} OR S.PARENT_IDS LIKE CONCAT('%',#{officeId},'%') )


